//            var x;
//            if (window.screen !== null && window.screen !== undefined){
//                x = window.screen.availWidth;
//            }
//            if (window.innerWidth !== null && window.innerWidth !== undefined) {
//                x = window.innerWidth;
//            }
//            if (window.body !== null && window.body !== undefined) {
//                x = window.body.clientWidth;
//            }
			return $(window).width();
