/*				if ( $cmp.find('.modwrap > a').length > 0 ) {
					$cmp.find('.modwrap > a').click( function(e) {
						if ( get_window_width() < 768 ) {
							return false;
						}
					});
				}*/

				resizedWindow();
				$(window).resize( function() {
					resizedWindow();
				});
				$cmp.find('.resp-arrow').parent().click( function() {
					toggleMobileMenu();
				});

				show_hide_first();
